mapper.select:
select count(*) 
from member 
where mem_no=#{mem_no} and mem_pw=#{mem_pw}

select count(*) 
from member 
where mem_email=#{mem_email}

mapper.update:
update member 
set mem_logindate=sysdate
where mem_no=#{mem_no}

update member 
set mem_pw=#{mem_pw} , mem_name=#{mem_name}
, mem_birth=#{mem_birth} , mem_tel=#{mem_tel}
, mem_address=#{mem_address} , mem_interest=#{mem_interest}
where mem_no=#{mem_no}

mapper.select:
select count(*) 
from history_money
where mem_no=#{mem_no}

mapper.update:
update member
set mem_money=mem_money+#{charge_money}
where mem_no=#{mem_no}

mapper.select:
select count(*)
from sell_data
where mem_no=#{mem_no}

select count(*)
from member
where mem_email=#{email}
